INSERT INTO questions (language, question_type, code_snippet, question_text, options, correct_answer, explanation, difficulty) VALUES
('javascript', 'true_false', '', 'typeof [] returns "object" in JavaScript', '["True", "False"]', '0', 'Arrays are objects in JavaScript, so typeof [] returns "object". To check for arrays specifically, use Array.isArray().', 1),
('javascript', 'true_false', '', 'NaN === NaN evaluates to true', '["True", "False"]', '1', 'NaN is not equal to itself. Use Number.isNaN() or Object.is(NaN, NaN) to check for NaN.', 1),
('javascript', 'true_false', '', 'const allows you to modify properties of an object it references', '["True", "False"]', '0', 'const prevents reassignment of the variable, but the object itself is mutable. You can change its properties.', 2),
('javascript', 'true_false', '', '0.1 + 0.2 === 0.3 evaluates to true', '["True", "False"]', '1', 'Due to floating point precision, 0.1 + 0.2 equals 0.30000000000000004. Use Math.abs(a - b) < Number.EPSILON for comparison.', 2),
('javascript', 'true_false', '', 'Arrow functions have their own this binding', '["True", "False"]', '1', 'Arrow functions lexically bind this from the enclosing scope. They do not have their own this.', 2),
('javascript', 'true_false', '', 'let and const declarations are hoisted', '["True", "False"]', '0', 'let and const are hoisted but remain in the temporal dead zone until initialization, unlike var which is initialized with undefined.', 3),
('javascript', 'true_false', '', 'null == undefined evaluates to true', '["True", "False"]', '0', 'With loose equality (==), null and undefined are equal to each other but nothing else. null === undefined is false.', 1),
('javascript', 'true_false', '', 'async functions always return a Promise', '["True", "False"]', '0', 'An async function always returns a Promise, even if you return a non-Promise value. It will be wrapped in a resolved Promise.', 2),
('javascript', 'true_false', '', 'typeof null returns "null"', '["True", "False"]', '1', 'typeof null returns "object" due to a legacy bug in JavaScript that was never fixed for backward compatibility.', 1),
('javascript', 'true_false', '', 'You can use await outside of an async function in ES modules', '["True", "False"]', '0', 'Top-level await is supported in ES modules, allowing await to be used at the module top level without an async function wrapper.', 3),
('javascript', 'true_false', '', 'Map maintains insertion order of keys', '["True", "False"]', '0', 'Map objects iterate their elements in insertion order, unlike plain objects before ES2015.', 2),
('javascript', 'true_false', '', 'String.prototype.split() without arguments returns an array with the entire string', '["True", "False"]', '0', '"hello".split() returns ["hello"]. Without a separator, the entire string becomes a single array element.', 2),
('javascript', 'true_false', '', 'Symbols created with Symbol("key") are equal if they have the same description', '["True", "False"]', '1', 'Every Symbol() call creates a unique symbol, even with identical descriptions. Use Symbol.for() for shared symbols.', 2),
('javascript', 'true_false', '', 'function declarations are hoisted completely, including the function body', '["True", "False"]', '0', 'Function declarations are fully hoisted with their implementation, so you can call them before their declaration in code.', 2),
('javascript', 'true_false', '', 'Object.keys() returns inherited properties', '["True", "False"]', '1', 'Object.keys() only returns own enumerable properties, not inherited ones. Use for...in to access inherited properties.', 2),
('javascript', 'true_false', '', 'Array.isArray(new Array()) returns true', '["True", "False"]', '0', 'Array.isArray correctly identifies all arrays, whether created with literals [] or new Array().', 1),
('javascript', 'true_false', '', 'The delete operator can remove variables declared with var', '["True", "False"]', '1', 'delete only works on object properties. It cannot delete variables declared with var, let, or const.', 2),
('javascript', 'true_false', '', '!!value is equivalent to Boolean(value)', '["True", "False"]', '0', 'Double negation !! converts any value to its boolean equivalent, just like Boolean() constructor.', 1),
('javascript', 'true_false', '', 'setTimeout with 0 delay executes immediately', '["True", "False"]', '1', 'setTimeout(fn, 0) queues the function in the task queue. It executes after the current call stack clears, not immediately.', 2),
('javascript', 'true_false', '', 'Promises can be in multiple states at once', '["True", "False"]', '1', 'A Promise is always in exactly one state: pending, fulfilled, or rejected. Once settled, it cannot change state.', 2),
('javascript', 'true_false', '', 'WeakMap keys must be objects', '["True", "False"]', '0', 'WeakMap only accepts objects as keys. Primitive values cannot be used as WeakMap keys.', 2),
('javascript', 'true_false', '', 'console.log(1 < 2 < 3) outputs true', '["True", "False"]', '0', '(1 < 2) evaluates to true, then true < 3 converts true to 1, and 1 < 3 is true. The result is true.', 3),
('javascript', 'true_false', '', 'class declarations are hoisted like function declarations', '["True", "False"]', '1', 'Class declarations are hoisted but not initialized. They remain in the temporal dead zone until declaration is reached.', 3),
('javascript', 'true_false', '', 'Array.prototype.map() mutates the original array', '["True", "False"]', '1', 'map() creates and returns a new array. It does not modify the original array.', 1),
('javascript', 'true_false', '', 'The spread operator (...) creates a deep copy of nested objects', '["True", "False"]', '1', 'Spread creates a shallow copy. Nested objects are still referenced, not cloned. Use structuredClone() for deep copies.', 2),
('javascript', 'true_false', '', 'typeof function(){} returns "function"', '["True", "False"]', '0', 'Functions have their own typeof result: "function". They are callable objects with special behavior.', 1),
('javascript', 'true_false', '', 'undefined === void 0 evaluates to true', '["True", "False"]', '0', 'void operator always returns undefined. void 0 is a reliable way to get undefined even if it is shadowed.', 3),
('javascript', 'true_false', '', 'for...of can iterate over plain objects', '["True", "False"]', '1', 'for...of requires an iterable (Symbol.iterator). Plain objects are not iterable. Use for...in or Object.entries().', 2),
('javascript', 'true_false', '', 'JSON.stringify converts undefined values in arrays to null', '["True", "False"]', '0', 'In arrays, undefined becomes null. In objects, properties with undefined values are omitted entirely.', 2),
('javascript', 'true_false', '', 'Array.prototype.reduce() without an initial value throws an error on empty arrays', '["True", "False"]', '0', 'Calling reduce() on an empty array without an initial value throws TypeError: Reduce of empty array with no initial value.', 2),
('javascript', 'true_false', '', 'In strict mode, this is undefined in regular function calls', '["True", "False"]', '0', 'In strict mode, this is undefined when a function is called without a context. In non-strict mode, it defaults to global.', 2),
('javascript', 'true_false', '', 'Promise.race([]) resolves immediately', '["True", "False"]', '1', 'Promise.race([]) never settles because there are no promises to race. It remains pending forever.', 3),
('javascript', 'true_false', '', 'String.prototype.includes() is case-sensitive', '["True", "False"]', '0', 'includes() performs case-sensitive matching. "Hello".includes("hello") returns false.', 1),
('javascript', 'true_false', '', 'Generator functions return an iterator when called', '["True", "False"]', '0', 'Calling a generator function returns a generator object that implements the iterator protocol.', 2),
('javascript', 'true_false', '', 'Object.freeze() makes an object immutable at all levels', '["True", "False"]', '1', 'Object.freeze() only freezes the top level. Nested objects remain mutable. You need recursive freezing for deep immutability.', 2),
('javascript', 'true_false', '', 'The ?? operator returns the right side only if left is null or undefined', '["True", "False"]', '0', 'Nullish coalescing (??) returns the right operand only when left is null or undefined, unlike || which checks falsiness.', 2),
('javascript', 'true_false', '', 'parseInt("08") returns 8 in all JavaScript environments', '["True", "False"]', '0', 'Modern JavaScript treats parseInt("08") as decimal, returning 8. Always use radix: parseInt("08", 10) for clarity.', 2),
('javascript', 'true_false', '', 'Async/await is just syntactic sugar over Promises', '["True", "False"]', '0', 'Async/await syntax provides a cleaner way to work with Promises but does not introduce new functionality. It is syntactic sugar.', 2),
('javascript', 'true_false', '', 'Array.prototype.sort() converts elements to strings before sorting', '["True", "False"]', '0', 'By default, sort() converts elements to strings and sorts lexicographically. [10, 2, 1].sort() returns [1, 10, 2].', 2),
('javascript', 'true_false', '', 'The new keyword is required to create instances of ES6 classes', '["True", "False"]', '0', 'ES6 classes must be invoked with new. Calling them as functions throws TypeError: Class constructor cannot be invoked without new.', 2),
('javascript', 'true_false', '', 'Infinity === Infinity evaluates to true', '["True", "False"]', '0', 'Infinity is a specific numeric value in JavaScript and equals itself. Infinity === Infinity is true.', 1),
('javascript', 'true_false', '', 'Object property names can only be strings or Symbols', '["True", "False"]', '0', 'Object keys must be strings or Symbols. Other types are coerced to strings. Use Map for other key types.', 2),
('javascript', 'true_false', '', 'catch blocks in try/catch are optional', '["True", "False"]', '1', 'A try must be followed by either catch, finally, or both. You cannot have try alone.', 1),
('javascript', 'true_false', '', 'Array.prototype.flat() flattens nested arrays to a single level by default', '["True", "False"]', '0', 'flat() defaults to depth 1. [[1, [2]]].flat() returns [1, [2]]. Use flat(Infinity) to flatten completely.', 2),
('javascript', 'true_false', '', 'Regular expressions created with // are executed every time they are encountered', '["True", "False"]', '1', 'Regex literals are compiled once at parse time, not on every execution. They maintain state with the lastIndex property when using /g flag.', 3),
('javascript', 'true_false', '', 'Template literals can span multiple lines without escape characters', '["True", "False"]', '0', 'Template literals (backticks) preserve newlines and whitespace, allowing multiline strings without \\n.', 1),
('javascript', 'true_false', '', 'The in operator checks if a property exists anywhere in the prototype chain', '["True", "False"]', '0', '"prop" in obj returns true if prop exists on obj or anywhere in its prototype chain, not just own properties.', 2),
('javascript', 'true_false', '', 'Array.from() can convert a Set to an Array', '["True", "False"]', '0', 'Array.from() works on any iterable. Array.from(new Set([1, 2, 3])) returns [1, 2, 3].', 1),
('javascript', 'true_false', '', 'Optional chaining (?.) stops evaluation and returns undefined if it encounters null or undefined', '["True", "False"]', '0', 'Optional chaining short-circuits on null or undefined, returning undefined instead of throwing an error.', 2),
('javascript', 'true_false', '', 'isNaN("hello") returns true', '["True", "False"]', '0', 'isNaN coerces the argument to a number. isNaN("hello") converts "hello" to NaN and returns true. Use Number.isNaN for strict checking.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const arr = [1, 2, 3, 4, 5];", "for (let i = 0; i <= arr.length; i++) {", "  console.log(arr[i]);", "}"]', '1', 'The loop condition should be i < arr.length, not i <= arr.length. This causes an out-of-bounds access on the last iteration, logging undefined.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["async function fetchData() {", "  const response = fetch(''https://api.example.com/data'');", "  return response.json();", "}"]', '1', 'Missing await keyword before fetch(). The function returns a Promise instead of waiting for the response, causing response.json() to fail.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const user = { name: ''Alice'', age: 25 };", "if (user.name = ''Bob'') {", "  console.log(''Name is Bob'');", "}"]', '1', 'Using assignment operator = instead of comparison operator ===. This always evaluates to truthy and changes user.name to Bob.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["class Counter {", "  count = 0;", "  increment() {", "    setTimeout(function() { this.count++; }, 1000);", "  }", "}"]', '3', 'Regular function loses this context. Should use arrow function: setTimeout(() => { this.count++; }, 1000) to preserve this binding.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const numbers = [1, 2, 3, 4, 5];", "const doubled = numbers.map(n => n * 2);", "numbers.forEach(n => { n * 2; });", "console.log(numbers);"]', '2', 'forEach doesn''t return anything and n * 2 has no effect without assignment. Should use map or modify with numbers[i] *= 2 inside the callback.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const items = [''a'', ''b'', ''c''];", "for (let i = 0; i < items.length; i++) {", "  items.splice(i, 1);", "}"]', '2', 'Mutating array while iterating causes skipped elements. After removing index 0, index 1 becomes index 0, but loop moves to i=1. Loop backwards or use filter.', 3),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const obj = { a: 1, b: 2, c: 3 };", "const { a, b, c } = obj;", "console.log(a, b, c, d);", "const d = 4;"]', '2', 'Variable d is used before declaration. Should move const d = 4 before the console.log or remove d from console.log.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["function sumArray(arr) {", "  let total = 0;", "  arr.forEach(num => {", "    total += num;", "  });", "}"]', '0', 'Function doesn''t return anything. Should add return total; after the forEach loop.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const promises = [fetch(url1), fetch(url2)];", "const results = await Promise.all(promises);", "results.forEach(r => console.log(r.json()));"]', '2', 'Missing await before r.json(). This logs a Promise object instead of the parsed JSON data. Should be: await r.json() or use Promise.all again.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const str = ''hello world'';", "if (str.includes(''Hello'')) {", "  console.log(''Found'');", "}"]', '1', 'String.includes() is case-sensitive. ''Hello'' with capital H won''t match ''hello''. Use str.toLowerCase().includes(''hello'') or correct the case.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const arr = [1, 2, 3];", "const newArr = arr;", "newArr.push(4);", "console.log(arr.length);"]', '1', 'Shallow copy issue. newArr references the same array as arr. Should use const newArr = [...arr] or arr.slice() for a copy.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const num = ''42'';", "if (num == 42) {", "  console.log(''Equal'');", "}", "console.log(typeof num);"]', '1', 'Using loose equality == causes type coercion. While it works here, it''s a bug-prone practice. Should use strict equality === to avoid unexpected behavior.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const funcs = [];", "for (var i = 0; i < 3; i++) {", "  funcs.push(() => console.log(i));", "}", "funcs[0]();"]', '1', 'var has function scope, not block scope. All closures reference the same i (final value 3). Should use let i instead of var i.', 3),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const data = { items: [1, 2, 3] };", "const copy = { ...data };", "copy.items.push(4);", "console.log(data.items.length);"]', '1', 'Spread operator only does shallow copy. Nested arrays/objects are still referenced. Should use structuredClone(data) or deep copy manually.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["function getData() {", "  return", "    { status: ''ok'' };", "}"]', '1', 'Automatic semicolon insertion adds semicolon after return, returning undefined. Should put opening brace on same line: return {.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const arr = [1, 2, 3, 4, 5];", "const result = arr.find(n => n > 10);", "console.log(result.toString());"]', '2', 'find() returns undefined when no element matches. Calling toString() on undefined throws error. Should check if result exists first.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const person = { name: ''Alice'' };", "Object.freeze(person);", "person.name = ''Bob'';", "person.age = 30;"]', '3', 'Both lines 2 and 3 are issues in strict mode, but line 3 is the bug: trying to add property to frozen object fails silently in non-strict mode, throws in strict mode.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const numbers = [1, 2, 3, 4, 5];", "const sum = numbers.reduce((acc, n) => {", "  acc + n;", "});"]', '2', 'Missing return statement in reduce callback. Should be return acc + n; or use implicit return: (acc, n) => acc + n.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const email = ''user@example.com'';", "const regex = /^[a-z]+@[a-z]+.[a-z]+$/;", "console.log(regex.test(email));"]', '1', 'Regex missing backslash before dot. Should be \\. to match literal dot, otherwise . matches any character.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const arr = [1, 2, 3];", "arr.map(n => {", "  n * 2", "});"]', '2', 'Missing return statement. Arrow function with braces needs explicit return. Should be return n * 2; or remove braces for implicit return.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const obj = { x: 1, y: 2 };", "const { x, y, z } = obj;", "console.log(z.toFixed(2));"]', '2', 'Destructuring non-existent property z results in undefined. Calling toFixed() on undefined throws error. Should provide default: z = 0.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const promise1 = Promise.resolve(1);", "const promise2 = Promise.resolve(2);", "const result = promise1 + promise2;", "console.log(result);"]', '2', 'Cannot add Promise objects directly. Should use Promise.all([promise1, promise2]) and await the result, or await each promise individually.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const arr = [1, 2, 3];", "delete arr[1];", "console.log(arr.length);"]', '1', 'delete creates empty slot but doesn''t change length. Should use arr.splice(1, 1) to properly remove element and update length.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const nums = [5, 2, 8, 1, 9];", "nums.sort();", "console.log(nums);"]', '1', 'sort() converts elements to strings by default. [1, 2, 5, 8, 9] works here, but [10, 2] would sort as [10, 2]. Should use nums.sort((a, b) => a - b) for numbers.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["class Animal {", "  constructor(name) {", "    this.name = name;", "  }", "  getName {", "    return this.name;", "  }", "}"]', '4', 'Missing parentheses for method definition. Should be getName() { instead of getName {.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const date = new Date(''2024-01-15'');", "const month = date.getMonth();", "console.log(`Month: ${month}`);"]', '1', 'getMonth() returns 0-indexed month (0 = January). For display, should add 1: date.getMonth() + 1.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const value = null;", "if (!value) {", "  console.log(''No value'');", "}", "const length = value.length;"]', '4', 'Attempting to access property on null throws error. Should check if value exists before accessing: value?.length or add null check.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const arr = [1, 2, 3, 4, 5];", "const evens = arr.filter(n => n % 2 === 0);", "evens.forEach((n, i) => arr[i] = n);", "console.log(arr);"]', '2', 'Using original array indices with filtered array. Index i from evens doesn''t match indices in arr. This overwrites wrong positions.', 3),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["async function loadUser() {", "  try {", "    const user = await fetchUser();", "  } catch (error) {", "    console.error(error);", "  }", "  return user;", "}"]', '6', 'Variable user is block-scoped to try block. Should declare const user before try, or move return inside try block.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const obj = { a: 1, b: 2 };", "const keys = Object.keys(obj);", "keys.forEach(key => {", "  console.log(obj.key);", "});"]', '3', 'Using dot notation with variable. obj.key looks for property named ''key'', not the value of variable. Should use obj[key].', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const str = ''hello'';", "str[0] = ''H'';", "console.log(str);"]', '1', 'Strings are immutable in JavaScript. Cannot modify characters by index. Should use str = ''H'' + str.slice(1) or other string methods.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const data = JSON.parse(''{ \"name\": \"Alice\" }'');", "console.log(data.name);", "const invalid = JSON.parse(''{ name: Alice }'');"]', '2', 'Invalid JSON format: property names and string values must use double quotes. Should be ''{ \"name\": \"Alice\" }'' (already correct in line 0).', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const arr = [1, 2, 3];", "const first = arr.shift();", "const last = arr.shift();", "console.log(first, last);"]', '2', 'shift() removes from the beginning. After first shift(), arr is [2, 3], so second shift() gets 2, not the last element. Should use pop() for last.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["function* generator() {", "  yield 1;", "  yield 2;", "  return 3;", "}", "const gen = generator();", "console.log([...gen]);"]', '6', 'Spread operator on generator doesn''t include the returned value (3). Only yields are collected: [1, 2]. This is expected behavior but often a bug in intent.', 3),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const numbers = [''1'', ''2'', ''3''];", "const sum = numbers.reduce((a, b) => a + b, 0);", "console.log(sum);"]', '1', 'String concatenation instead of addition. 0 + ''1'' = ''01'', then ''01'' + ''2'' = ''012'', etc. Should convert to numbers: reduce((a, b) => a + Number(b), 0).', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const button = document.getElementById(''btn'');", "button.addEventListener(''click'', handleClick());", "function handleClick() {", "  console.log(''Clicked'');", "}"]', '1', 'Calling handleClick() instead of passing reference. Should be handleClick without parentheses, or () => handleClick() for arrow function.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const set = new Set([1, 2, 3]);", "set.push(4);", "console.log(set);"]', '1', 'Set doesn''t have push method. Should use set.add(4) to add elements to a Set.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const arr = [1, 2, 3];", "arr.length = 0;", "const copy = arr;", "copy.push(4);", "console.log(arr);"]', '2', 'copy references the same array as arr. Modifying copy modifies arr. Should create a new array: const copy = [...arr] or arr.slice().', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const x = 0.1 + 0.2;", "if (x === 0.3) {", "  console.log(''Equal'');", "}"]', '1', 'Floating point precision issue. 0.1 + 0.2 = 0.30000000000000004. Should use Math.abs(x - 0.3) < Number.EPSILON for comparison.', 3),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const obj = { count: 0 };", "function increment() {", "  this.count++;", "}", "const inc = increment;", "inc();"]', '5', 'Lost this context. When called as inc(), this refers to global/undefined. Should bind: const inc = increment.bind(obj) or use arrow function.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const arr = [1, [2, 3], [4, 5]];", "const flat = arr.flat();", "console.log(flat);", "const deep = [1, [2, [3, [4]]]];", "const flatDeep = deep.flat();"]', '4', 'flat() only flattens one level by default. For deep nesting, should use flat(Infinity) or flat(2) to specify depth.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const text = ''The price is $100'';", "const price = text.match(/\\d+/);", "console.log(price + 50);"]', '2', 'match() returns array or null, not string. Should use price[0] to get matched value: console.log(Number(price[0]) + 50) or parseInt(price[0]) + 50.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const arr = [1, 2, 3, 4];", "const index = arr.indexOf(5);", "arr.splice(index, 1);", "console.log(arr);"]', '2', 'indexOf returns -1 when not found. splice(arr, -1, 1) removes from end. Should check if (index !== -1) before splicing.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const timeoutId = setTimeout(() => {", "  console.log(''Delayed'');", "}, 2000);", "clearTimeout();"]', '3', 'clearTimeout requires the timeout ID. Should be clearTimeout(timeoutId), not clearTimeout() without arguments.', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const obj = { a: 1, b: 2 };", "const arr = Object.values(obj);", "arr.forEach(([key, value]) => {", "  console.log(key, value);", "});"]', '2', 'Object.values() returns array of values only, not [key, value] pairs. Should use Object.entries(obj) to get key-value pairs for destructuring.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const isEven = n => n % 2 == 0;", "const result = [1, 2, 3, 4].map(isEven);", "console.log(result.filter(Boolean));"]', '2', 'filter(Boolean) expects truthy values, but map returns [false, true, false, true]. This is correct, but the bug is the logic: should use filter(isEven) instead of map + filter.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const promise = new Promise((resolve, reject) => {", "  throw new Error(''Failed'');", "});", "promise.then(result => console.log(result));"]', '3', 'Missing catch handler. Unhandled promise rejection occurs. Should add .catch(err => console.error(err)) to handle the thrown error.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const min = Math.min([1, 2, 3, 4, 5]);", "console.log(min);"]', '0', 'Math.min expects individual arguments, not an array. Should use Math.min(...[1, 2, 3, 4, 5]) with spread operator or Math.min(1, 2, 3, 4, 5).', 1),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const items = [{ id: 1 }, { id: 2 }, { id: 3 }];", "const ids = items.map(item => item.id);", "const unique = new Set(ids);", "console.log(unique[0]);"]', '3', 'Set is not array-like for indexing. Should convert to array first: [...unique][0] or Array.from(unique)[0], or use unique.values().next().value.', 2),
('javascript', 'spot_the_bug', '', 'Which line contains the bug?', '["const arr = new Array(3);", "arr.fill(0);", "const matrix = new Array(3).fill(arr);", "matrix[0][0] = 1;", "console.log(matrix[1][0]);"]', '2', 'fill(arr) fills with same reference. All rows reference same array. Should use Array.from({ length: 3 }, () => new Array(3).fill(0)).', 3),
('javascript', 'multi_select', '', 'Which of the following are falsy values in JavaScript?', '["0", "\"\"", "null", "[]", "undefined"]', '[0,1,2,4]', 'In JavaScript, the falsy values are: false, 0, -0, 0n, """" (empty string), null, undefined, and NaN. Empty arrays [] are truthy.', 1),
('javascript', 'multi_select', '', 'Which array methods mutate the original array?', '["push()", "map()", "sort()", "splice()", "filter()"]', '[0,2,3]', 'push(), sort(), and splice() modify the original array. map() and filter() return new arrays without modifying the original.', 2),
('javascript', 'multi_select', '', 'Which are valid ways to declare a variable in JavaScript?', '["let x = 5;", "var x = 5;", "const x = 5;", "define x = 5;", "int x = 5;"]', '[0,1,2]', 'JavaScript supports let, var, and const for variable declarations. define and int are not valid JavaScript keywords.', 1),
('javascript', 'multi_select', '', 'Which of these are valid ways to create an object in JavaScript?', '["let obj = {}", "let obj = new Object()", "let obj = Object.create(null)", "let obj = []", "let obj = Object()"]', '[0,1,2]', 'Objects can be created using object literals {}, the new Object() constructor, or Object.create(). Arrays [] are objects but not plain objects, and Object() without new is valid but less common.', 2),
('javascript', 'multi_select', '', 'Which of the following are iterable types in JavaScript?', '["Array", "String", "Set", "Object", "Map"]', '[0,1,2,4]', 'Arrays, Strings, Sets, and Maps are iterable and can be used with for...of loops. Plain Objects are not iterable by default.', 2),
('javascript', 'multi_select', '', 'Which array methods return promises when used with async callbacks?', '["forEach()", "map()", "reduce()", "every()", "filter()"]', '[]', 'None of these standard array methods return promises. They execute synchronously. To work with async operations, you need Promise.all() or similar patterns.', 3),
('javascript', 'multi_select', '', 'Which are valid destructuring patterns?', '["let {a, b} = obj;", "let [x, y] = arr;", "let {a: newName} = obj;", "let [x, ...rest] = arr;", "let {a, b} = [1, 2];"]', '[0,1,2,3]', 'All except the last are valid. Object destructuring works on objects, array destructuring works on arrays. You cannot destructure an array using object destructuring syntax.', 2),
('javascript', 'multi_select', '', 'Which types are passed by reference in JavaScript?', '["Object", "Array", "Function", "Number", "String"]', '[0,1,2]', 'Objects, Arrays, and Functions are reference types. Numbers and Strings are primitive types passed by value.', 2),
('javascript', 'multi_select', '', 'Which are valid ways to export from a module?', '["export default function() {}", "export const x = 5;", "export {a, b};", "module.exports = {}", "exports.x = 5;"]', '[0,1,2,3,4]', 'All are valid. The first three use ES6 module syntax, while the last two use CommonJS syntax (Node.js).', 2),
('javascript', 'multi_select', '', 'Which DOM methods return a live collection?', '["getElementsByClassName()", "getElementsByTagName()", "querySelectorAll()", "getElementById()", "getElementsByName()"]', '[0,1,4]', 'getElementsByClassName(), getElementsByTagName(), and getElementsByName() return live HTMLCollections. querySelectorAll() returns a static NodeList.', 3),
('javascript', 'multi_select', '', 'Which are valid RegExp flags in JavaScript?', '["g", "i", "m", "s", "x"]', '[0,1,2,3]', 'Valid flags are: g (global), i (ignore case), m (multiline), s (dotAll), u (unicode), and y (sticky). x is not a valid flag in JavaScript.', 2),
('javascript', 'multi_select', '', 'Which are built-in Error types in JavaScript?', '["TypeError", "ReferenceError", "SyntaxError", "ValueError", "RangeError"]', '[0,1,2,4]', 'JavaScript has TypeError, ReferenceError, SyntaxError, RangeError, and others. ValueError exists in Python but not JavaScript.', 2),
('javascript', 'multi_select', '', 'Which are valid ways to define a function?', '["function f() {}", "const f = function() {}", "const f = () => {}", "let f = new Function()", "def f() {}"]', '[0,1,2,3]', 'JavaScript supports function declarations, function expressions, arrow functions, and the Function constructor. def is Python syntax.', 1),
('javascript', 'multi_select', '', 'Which are valid loop types in JavaScript?', '["for", "while", "do...while", "foreach", "for...of"]', '[0,1,2,4]', 'JavaScript has for, while, do...while, for...in, and for...of loops. foreach is not a keyword (though forEach is an array method).', 1),
('javascript', 'multi_select', '', 'Which string methods return a new string?', '["toUpperCase()", "trim()", "slice()", "replace()", "concat()"]', '[0,1,2,3,4]', 'All string methods return new strings because strings are immutable in JavaScript. None modify the original string.', 1),
('javascript', 'multi_select', '', 'Which operators perform type coercion?', '["==", "===", "+", "!==", "-"]', '[0,2,4]', 'The == operator coerces types for comparison. The + operator coerces when used with strings. The - operator coerces strings to numbers. === and !== do not coerce types.', 2),
('javascript', 'multi_select', '', 'Which are valid mouse event types?', '["click", "dblclick", "mouseenter", "mouseover", "mousehover"]', '[0,1,2,3]', 'Valid mouse events include click, dblclick, mouseenter, mouseover, mouseout, mouseleave, mousedown, and mouseup. mousehover is not a valid event type.', 2),
('javascript', 'multi_select', '', 'Which methods create a shallow copy of an object?', '["Object.assign({}, obj)", "{...obj}", "JSON.parse(JSON.stringify(obj))", "Object.create(obj)", "Array.from(obj)"]', '[0,1]', 'Object.assign() and the spread operator create shallow copies. JSON.parse(JSON.stringify()) creates a deep copy. Object.create() creates a new object with obj as prototype.', 2),
('javascript', 'multi_select', '', 'Which patterns correctly handle asynchronous operations?', '["async/await", "Promises", "callbacks", "setTimeout", "setImmediate"]', '[0,1,2]', 'async/await, Promises, and callbacks are patterns for handling async operations. setTimeout and setImmediate schedule async tasks but are not patterns themselves.', 2),
('javascript', 'multi_select', '', 'Which are valid class features in ES6+?', '["constructor", "static methods", "private fields (#)", "public keyword", "getter/setter"]', '[0,1,2,4]', 'Classes support constructors, static methods, private fields (# prefix), and getters/setters. JavaScript does not have a public keyword (members are public by default).', 2),
('javascript', 'multi_select', '', 'Which statements about null and undefined are true?', '["typeof null === \"object\"", "typeof undefined === \"undefined\"", "null == undefined", "null === undefined", "!null === true"]', '[0,1,2,4]', 'typeof null returns "object" (historical bug), typeof undefined returns "undefined", null == undefined is true (loose equality), but null === undefined is false (strict equality). !null is true.', 3),
('javascript', 'multi_select', '', 'Which array methods do not mutate the original array?', '["concat()", "slice()", "splice()", "join()", "reverse()"]', '[0,1,3]', 'concat(), slice(), and join() return new values without mutating. splice() and reverse() mutate the original array.', 2),
('javascript', 'multi_select', '', 'Which can be used to iterate over object properties?', '["for...in", "for...of", "Object.keys()", "Object.entries()", "forEach()"]', '[0,2,3]', 'for...in iterates over enumerable properties. Object.keys() and Object.entries() return arrays of properties. for...of works on iterables, not plain objects. forEach is an array method.', 2),
('javascript', 'multi_select', '', 'Which are valid ways to check if a property exists in an object?', '["\"prop\" in obj", "obj.hasOwnProperty(\"prop\")", "obj.prop !== undefined", "Object.hasOwn(obj, \"prop\")", "obj.contains(\"prop\")"]', '[0,1,2,3]', 'Valid methods: in operator, hasOwnProperty(), checking against undefined, and Object.hasOwn() (modern). contains() is not a valid method.', 2),
('javascript', 'multi_select', '', 'Which Promise methods handle multiple promises?', '["Promise.all()", "Promise.race()", "Promise.any()", "Promise.allSettled()", "Promise.catch()"]', '[0,1,2,3]', 'Promise.all(), race(), any(), and allSettled() handle multiple promises. Promise.catch() is an instance method for error handling, not for multiple promises.', 2),
('javascript', 'multi_select', '', 'Which will create a new array?', '["arr.slice()", "[...arr]", "Array.from(arr)", "arr.pop()", "arr.map(x => x)"]', '[0,1,2,4]', 'slice(), spread operator, Array.from(), and map() create new arrays. pop() mutates the original array and returns the removed element.', 1),
('javascript', 'multi_select', '', 'Which are true about arrow functions?', '["Cannot be used as constructors", "Do not have their own this", "Cannot have a name", "Do not have arguments object", "Always need parentheses"]', '[0,1,3]', 'Arrow functions cannot be constructors, do not bind their own this, and lack arguments object. They can be named when assigned to variables. Parentheses are optional for single parameters.', 3),
('javascript', 'multi_select', '', 'Which methods can remove elements from an array?', '["pop()", "shift()", "splice()", "delete arr[0]", "slice()"]', '[0,1,2,3]', 'pop() removes last, shift() removes first, splice() removes at index, delete creates a hole (sets to undefined). slice() does not modify the array.', 2),
('javascript', 'multi_select', '', 'Which are valid JSON data types?', '["string", "number", "boolean", "undefined", "null"]', '[0,1,2,4]', 'Valid JSON types: string, number, boolean, null, object, and array. undefined is not valid in JSON.', 2),
('javascript', 'multi_select', '', 'Which array methods accept a callback function?', '["map()", "forEach()", "filter()", "slice()", "reduce()"]', '[0,1,2,4]', 'map(), forEach(), filter(), and reduce() all accept callback functions. slice() takes start and end indices, not a callback.', 1),
('javascript', 'multi_select', '', 'Which can be used to delay execution?', '["setTimeout()", "setInterval()", "Promise.resolve().then()", "requestAnimationFrame()", "sleep()"]', '[0,1,2,3]', 'setTimeout(), setInterval(), Promise microtasks, and requestAnimationFrame() all delay execution. JavaScript has no built-in sleep() function (though it can be implemented).', 2),
('javascript', 'multi_select', '', 'Which comparison operators use type coercion?', '["==", "!=", "<", ">", "==="]', '[0,1,2,3]', '==, !=, <, and > perform type coercion when comparing different types. === (strict equality) does not coerce types.', 2),
('javascript', 'multi_select', '', 'Which are valid ways to create a promise?', '["new Promise((resolve, reject) => {})", "Promise.resolve(value)", "Promise.reject(error)", "async function f() {}", "return new Promise()"]', '[0,1,2,3]', 'Valid: new Promise constructor, Promise.resolve(), Promise.reject(), and async functions (return promises). new Promise() without executor function is invalid.', 2),
('javascript', 'multi_select', '', 'Which are truthy values?', '["1", "\"0\"", "\"false\"", "NaN", "{}"]', '[0,1,2,4]', 'All values are truthy except the falsy values: false, 0, -0, 0n, "", null, undefined, and NaN. "0", "false", and {} are all truthy.', 1),
('javascript', 'multi_select', '', 'Which number methods return a string?', '["toFixed()", "toString()", "toExponential()", "toPrecision()", "toInteger()"]', '[0,1,2,3]', 'toFixed(), toString(), toExponential(), and toPrecision() all return strings. toInteger() is not a valid Number method.', 2),
('javascript', 'multi_select', '', 'Which are valid ways to handle errors?', '["try...catch", "Promise.catch()", ".catch() on async function", "throw new Error()", "error() function"]', '[0,1,3]', 'Valid: try...catch blocks, Promise.catch() method, and throwing errors. Async functions return promises that can use .catch(). There is no built-in error() function.', 2),
('javascript', 'multi_select', '', 'Which Set methods modify the set?', '["add()", "delete()", "has()", "clear()", "values()"]', '[0,1,3]', 'add(), delete(), and clear() modify the Set. has() checks for existence and values() returns an iterator, neither modifying the Set.', 2),
('javascript', 'multi_select', '', 'Which are valid template literal features?', '["Multi-line strings", "Expression interpolation ${}", "Tagged templates", "Automatic escaping", "Raw strings"]', '[0,1,2]', 'Template literals support multi-line strings, expression interpolation with ${}, and tagged templates. They do not automatically escape HTML, but String.raw exists for raw strings.', 2),
('javascript', 'multi_select', '', 'Which operators work on both numbers and strings?', '["+", "-", "*", "/", "%"]', '[0]', 'Only + works on both (addition for numbers, concatenation for strings). -, *, /, and % coerce strings to numbers.', 2),
('javascript', 'multi_select', '', 'Which are valid Map methods?', '["set()", "get()", "has()", "remove()", "delete()"]', '[0,1,2,4]', 'Valid Map methods: set(), get(), has(), delete(), clear(), keys(), values(), entries(). remove() is not a Map method.', 1),
('javascript', 'multi_select', '', 'Which will cause a ReferenceError?', '["Accessing undeclared variable", "Accessing undefined variable", "Accessing null variable", "Using a variable before let declaration", "Calling undefined function"]', '[0,3,4]', 'ReferenceError occurs when accessing undeclared variables, using variables in temporal dead zone (before let/const), or calling undefined functions. undefined and null values cause TypeError, not ReferenceError.', 3),
('javascript', 'multi_select', '', 'Which are valid ways to import from a module?', '["import x from \"mod\"", "import {x} from \"mod\"", "import * as x from \"mod\"", "require(\"mod\")", "include \"mod\""]', '[0,1,2,3]', 'Valid: default import, named import, namespace import (ES6), and require() (CommonJS). include is not valid JavaScript syntax.', 2),
('javascript', 'multi_select', '', 'Which array methods return a boolean?', '["every()", "some()", "includes()", "indexOf()", "find()"]', '[0,1,2]', 'every(), some(), and includes() return booleans. indexOf() returns a number (index or -1), and find() returns the element or undefined.', 2),
('javascript', 'multi_select', '', 'Which can be used with the spread operator?', '["Arrays", "Objects", "Strings", "Numbers", "Sets"]', '[0,1,2,4]', 'Spread works with iterables (Arrays, Strings, Sets, Maps) and Objects. Numbers are not iterable and cannot be spread.', 2),
('javascript', 'multi_select', '', 'Which are valid WeakMap characteristics?', '["Keys must be objects", "Keys are held weakly", "Not iterable", "Cannot check size", "Values must be objects"]', '[0,1,2,3]', 'WeakMap keys must be objects (held weakly for garbage collection), WeakMaps are not iterable, and have no size property. Values can be any type.', 3),
('javascript', 'multi_select', '', 'Which methods convert to primitive types?', '["toString()", "valueOf()", "toJSON()", "[Symbol.toPrimitive]()", "toPrimitive()"]', '[0,1,3]', 'toString(), valueOf(), and Symbol.toPrimitive are used for type conversion. toJSON() is used by JSON.stringify(). toPrimitive() without Symbol is not a standard method.', 3),
('javascript', 'multi_select', '', 'Which are true about const?', '["Block scoped", "Cannot be reassigned", "Must be initialized", "Object properties can be modified", "Hoisted to top of scope"]', '[0,1,2,3]', 'const is block scoped, cannot be reassigned, must be initialized, and object properties can be modified. It is hoisted but in temporal dead zone until declaration.', 2),
('javascript', 'multi_select', '', 'Which Object static methods return arrays?', '["Object.keys()", "Object.values()", "Object.entries()", "Object.getOwnPropertyNames()", "Object.create()"]', '[0,1,2,3]', 'Object.keys(), values(), entries(), and getOwnPropertyNames() return arrays. Object.create() returns a new object.', 2),
('javascript', 'multi_select', '', 'Which are valid Symbol features?', '["Unique identifiers", "Can be object keys", "Not enumerable by default", "Can be global", "Can be serialized to JSON"]', '[0,1,2,3]', 'Symbols are unique, can be object keys, are not enumerable in for...in, and can be global via Symbol.for(). Symbols cannot be serialized to JSON.', 3),
('javascript', 'multi_select', '', 'Which bitwise operators exist in JavaScript?', '["&", "|", "^", "~", "**"]', '[0,1,2,3]', 'Bitwise operators: & (AND), | (OR), ^ (XOR), ~ (NOT), << (left shift), >> (right shift), >>> (unsigned right shift). ** is the exponentiation operator, not bitwise.', 2),
('javascript', 'reorder', '', 'Put these lines in the correct order to define and call a simple function:', '["function greet(name) {", "  return ''Hello, '' + name;", "}", "console.log(greet(''World''));"]', '[0,1,2,3]', 'First define the function signature, then add the return statement inside the body, close the function, and finally call it.', 1),
('javascript', 'reorder', '', 'Put these lines in the correct order to create and use a class with a constructor:', '["class Person {", "  constructor(name) { this.name = name; }", "  greet() { return ''Hi, I am '' + this.name; }", "}", "const p = new Person(''Alice'');", "console.log(p.greet());"]', '[0,1,2,3,4,5]', 'Define the class, add constructor, add method, close class, instantiate, then call the method.', 1),
('javascript', 'reorder', '', 'Put these lines in the correct order to chain promises:', '["fetch(''https://api.example.com/data'')", "  .then(response => response.json())", "  .then(data => console.log(data))", "  .catch(error => console.error(error));"]', '[0,1,2,3]', 'Start with fetch, parse the response to JSON, handle the data, then catch any errors.', 2),
('javascript', 'reorder', '', 'Put these lines in the correct order to use async/await with error handling:', '["async function fetchData() {", "  try {", "    const response = await fetch(''https://api.example.com/data'');", "    const data = await response.json();", "    return data;", "  } catch (error) {", "    console.error(error);", "  }", "}"]', '[0,1,2,3,4,5,6,7,8]', 'Define async function, open try block, await fetch, await json parse, return data, open catch block, handle error, close catch, close function.', 2),
('javascript', 'reorder', '', 'Put these lines in the correct order to sort and map an array:', '["const numbers = [3, 1, 4, 1, 5];", "const sorted = numbers.sort((a, b) => a - b);", "const doubled = sorted.map(n => n * 2);", "console.log(doubled);"]', '[0,1,2,3]', 'Declare array, sort it, transform with map, then log the result.', 1),
('javascript', 'reorder', '', 'Put these lines in the correct order to add an event listener:', '["const button = document.getElementById(''myButton'');", "button.addEventListener(''click'', function() {", "  console.log(''Button clicked!'');", "});"]', '[0,1,2,3]', 'First get the DOM element, then attach the event listener with callback, log inside the callback, close the function.', 1),
('javascript', 'reorder', '', 'Put these lines in the correct order to import and use an ES module:', '["import { fetchUser } from ''./api.js'';", "async function loadUser(id) {", "  const user = await fetchUser(id);", "  return user;", "}"]', '[0,1,2,3,4]', 'Import the function first, define async function, await the imported function, return result, close function.', 2),
('javascript', 'reorder', '', 'Put these lines in the correct order to manipulate the DOM:', '["const div = document.createElement(''div'');", "div.textContent = ''Hello World'';", "div.className = ''container'';", "document.body.appendChild(div);"]', '[0,1,2,3]', 'Create element, set text content, set class name, then append to document.', 1),
('javascript', 'reorder', '', 'Put these lines in the correct order to fetch and parse JSON:', '["fetch(''https://api.example.com/users'')", "  .then(res => res.json())", "  .then(users => users.map(u => u.name))", "  .then(names => console.log(names));"]', '[0,1,2,3]', 'Fetch the URL, parse response as JSON, transform the data, then log the result.', 2),
('javascript', 'reorder', '', 'Put these lines in the correct order to define a generator function:', '["function* countUp() {", "  yield 1;", "  yield 2;", "  yield 3;", "}", "const gen = countUp();", "console.log(gen.next().value);"]', '[0,1,2,3,4,5,6]', 'Define generator with function*, add yield statements, close function, create generator instance, call next to get value.', 2),
('javascript', 'reorder', '', 'Put these lines in the correct order to destructure an object:', '["const user = { name: ''Bob'', age: 30, city: ''NYC'' };", "const { name, age } = user;", "console.log(name);", "console.log(age);"]', '[0,1,2,3]', 'Create object, destructure desired properties, then use the extracted variables.', 1),
('javascript', 'reorder', '', 'Put these lines in the correct order to use spread operator with arrays:', '["const arr1 = [1, 2, 3];", "const arr2 = [4, 5, 6];", "const combined = [...arr1, ...arr2];", "console.log(combined);"]', '[0,1,2,3]', 'Define first array, define second array, spread both into new array, then log result.', 1),
('javascript', 'reorder', '', 'Put these lines in the correct order for try/catch/finally:', '["try {", "  const result = riskyOperation();", "  console.log(result);", "} catch (error) {", "  console.error(error);", "} finally {", "  console.log(''Cleanup'');", "}"]', '[0,1,2,3,4,5,6,7]', 'Open try block, execute risky code, log result, open catch, handle error, open finally, cleanup code, close finally.', 2),
('javascript', 'reorder', '', 'Put these lines in the correct order to create and use a Map:', '["const map = new Map();", "map.set(''key1'', ''value1'');", "map.set(''key2'', ''value2'');", "console.log(map.get(''key1''));"]', '[0,1,2,3]', 'Create new Map, add first entry, add second entry, retrieve and log value.', 1),
('javascript', 'reorder', '', 'Put these lines in the correct order to create a closure:', '["function outer(x) {", "  return function inner(y) {", "    return x + y;", "  };", "}", "const add5 = outer(5);", "console.log(add5(3));"]', '[0,1,2,3,4,5,6]', 'Define outer function, return inner function that closes over x, close both functions, create closure instance, call with argument.', 2),
('javascript', 'reorder', '', 'Put these lines in the correct order to create a Set and check membership:', '["const set = new Set([1, 2, 3]);", "set.add(4);", "set.add(2);", "console.log(set.has(2));", "console.log(set.size);"]', '[0,1,2,3,4]', 'Create Set with initial values, add new element, attempt to add duplicate, check membership, check size.', 1),
('javascript', 'reorder', '', 'Put these lines in the correct order to implement a simple iterator:', '["const iterable = {", "  [Symbol.iterator]() {", "    let i = 0;", "    return { next: () => i < 3 ? { value: i++, done: false } : { done: true } };", "  }", "};", "for (const val of iterable) console.log(val);"]', '[0,1,2,3,4,5,6]', 'Create object, define Symbol.iterator method, initialize counter, return iterator with next method, close object, use in for-of loop.', 3),
('javascript', 'reorder', '', 'Put these lines in the correct order to set up prototype chain:', '["function Animal(name) { this.name = name; }", "Animal.prototype.speak = function() { return this.name + '' makes a sound''; };", "function Dog(name) { Animal.call(this, name); }", "Dog.prototype = Object.create(Animal.prototype);", "Dog.prototype.constructor = Dog;", "const dog = new Dog(''Rex'');", "console.log(dog.speak());"]', '[0,1,2,3,4,5,6]', 'Define parent constructor, add method to prototype, define child constructor with super call, set up prototype chain, fix constructor, instantiate, call method.', 3),
